import React from 'react';
import styles from '../styles/Card.module.css';

const Card = ({ title, cover }) => {
  return (
    <div className={styles.card}>
      <img src={cover} alt={title} className={styles.cardImage} />
      <h3 className={styles.cardTitle}>{title}</h3>
    </div>
  );
};

export default Card;
